import React, { useState } from 'react'

const Contador = () => {

    const [count, setCount] = useState(0)
    const increase = () => {
        console.log('Click')
        setCount (count + 1)
    }
    const decrease = () => {
        console.log('Click')
        //Evitar número negativos
        setCount (count - 1)
    }
    /* const reset = () => {

    } */


    return (
        <div>
            { count }
            <button onClick = { increase }>+</button>
            <button onClick = { () => decrease()}>-</button>
            <button>Reset</button>
        </div>
    )
}

export default Contador
