import React, { useState, useEffect } from 'react'
import { db, auth } from '../../firebase'
import { v4 as uuidv4 } from 'uuid'
import Masonry from 'react-masonry-css'
import './Note.css'

function Note(){

    const [ incoming, setIncoming ] = useState([])

    const getOrders = () =>{
        return db
        .collection('orders')
        .where("status", "==", "in progress")
        .orderBy('date', 'desc')
        .onSnapshot((querySnapshot) => {
            const docs = []
            querySnapshot.forEach((doc) => {
                docs.push({...doc.data(), id: doc.id})
            })
            setIncoming(docs)
        })
    } 

    useEffect(() => {
        let unsubscribe = getOrders()

        return () => {
            unsubscribe()
        }
      }, []);

    const updateOrder = (id) => {
        db    
        .collection('orders')
        .doc(id)
        .update({
            status: "done"
        })
        .then(() => {
            alert('un meserx ser치 notificado :)')
        })
        .catch(() =>{
            alert('Ocurri칩 un error, intentalo de nuevo en un momento')
        })   
    }

    let incomingOrders = incoming.map( order => {
        console.log(order.date)
        return (
            <div className="notes" key={order.id}>
                <h2 className="table"> Mesa {order.table}</h2>
                <p> Meserx: {order.waiter}</p>

                    {order.items.map(product => {
                        return (
                            <div className="products-on-note" key={uuidv4()} >
                            <ul className="note-ul">
                                <li className="product-name">{product.item}</li>
                            </ul>
                                <p className="product-quantity">{product.quantity}</p>
                            </div>
                        )
                    })}

                <button className="green" onClick={() => {console.log(order); updateOrder(order.id)}}>Orden lista!</button>
            </div>
        )
    })

    // breakpoints for responsive CSS Masonry
    const breakpointColumnsObj = {
        default: 3,
        1100: 3,
        700: 2,
        500: 1
      };

    return(
        <div className='container'>
            <Masonry
                breakpointCols={breakpointColumnsObj}
                className="my-masonry-grid"
                columnClassName="my-masonry-grid_column">
                    {incomingOrders}
            </Masonry>
        </div>
    )
}

export default Note
