import React from 'react'

const addProduct = (product) => {
    if (order.items.find(item => item.id === product.id)) {
       // order.items.map( item => item.id === product.id )
    } else{
        setOrder({
            ...order,
            items: [
                ...order.items,
                {...product, quantity: 1}
            ]
        })
    }
}

export default addProduct
