import React from 'react';
//Router
import {
  BrowserRouter as Router,
  //Con switch se crea el contenido dinámico, adentro va el route 
  Switch,
  Route,
  //Link es un ancla
  Link,
  NavLink
} from "react-router-dom";

import background from './img/background.png'
import './App.css';

function App() {
  return (
    <Router>
      <div className="App">
        {/* <img src={background}/> */}
        <h1>Elementos permanentes (navbar o quizá los iconos)</h1>
        <hr />
          <Switch>
            <Route path = "/rol">
              Escoge entre mesero o cocinero
            </Route>
            <Route path = "/waiter">
              Pantalla mesero
            </Route>
            <Route path = "/menu">
              Pantalla menu: desayuno y burger
            </Route>
            <Route path = "/menu-breakfast">
              Pantalla breakfast
            </Route>
            <Route path = "/menu-burger">
              Pantalla burger
            </Route>
            <Route path = "/chef">
              Pantalla chef
            </Route>
            <Route path = "/orders">
              Pantalla comandas
            </Route>
            <Route path = "/">
              {/* Aqui se llaman los componentes */}
              Esta es la pagina de inicio, o sea el main
            </Route>
          </Switch>
    </div>
    </Router>

  );
}

export default App;
