import React from 'react';
//Router
import {
  BrowserRouter as Router,
  //Con switch se crea el contenido dinámico, adentro va el route 
  Switch,
  Route,
  //Link es un ancla
  Link,
  NavLink
} from "react-router-dom";


import './App.css';
import Main from './components/Main';
import Waiter from './components/Waiter';
import Role from './components/Role';
import Menu from './components/Menu';
import MenuBreakfast from './components/MenuBreakfast'
import MenuBurger from './components/MenuBurger'
import Chef from './components/Chef';
import Orders from './components/Orders';


function App() {
  return (
    <Router>
      <div className="container mt-5">
        <div className='btn-group'>
          <Link to = '/' className= 'btn btn-dark'>
            Main
          </Link>
          <NavLink to = '/role' className= 'btn btn-dark' activeClassName='active'>
            M & C
          </NavLink>
        </div>
        <hr />
          <Switch>
            <Route path = "/role">
              <Role />
            </Route>
            <Route path = "/waiter">
              <Waiter />
            </Route>
            <Route path = "/menu">
              <Menu />
            </Route>
            <Route path = "/menu-breakfast">
              <MenuBreakfast />
            </Route>
            <Route path = "/menu-burger">
              <MenuBurger />
            </Route>
            <Route path = "/chef">
              <Chef />
            </Route>
            <Route path = "/orders">
              <Orders />
            </Route>
            <Route path = "/">
              {/* Aqui se llaman los componentes */}
              <Main />
            </Route>
          </Switch>
    </div>
    </Router>

  );
}

export default App;
