import { db, auth } from '../../firebase'
import './Note.css'

function Note(){

    const [ incoming, setIncoming ] = useState([])

    const getOrders = () =>{
        db
        .collection('orders')
        .onSnapshot((querySnapshot) => {
            querySnapshot.forEach((doc) => {
                console.log(doc.data())
            })
        })
    }

    useEffect(() => {
        console.log('hola desde use Effect')
        getOrders()
      }, []);

    return(
        <div className="note-container">
            hola desde note
        </div>
    )
}

export default Note
