import React, { useState } from "react";

//Router
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

//Styles
import "./components/utils/styles.css";

//Components
import Main from "./components/Main";
import Role from "./components/Role";
import Waiter from "./components/Waiter";
import Chef from "./components/Chef";
import Menu from "./components/Menu";
import MenuBreakfast from "./components/Breakfast";
import MenuBurger from "./components/Burger";
import Orders from "./components/Orders";
import Order from "./components/Order/Order.js";

//Recordar que usamos arreglos
//SI ya existe enonces a quantity agrega 1
//Si NO existe entonces crea uno nuevo

//JSON
import Data from "./components/utils/Data/Data.json";

function App() {
  const [order, setOrder] = useState([]);

  const addingProductToOrder = (product) => {
    // se busca en la order el item , si ya existe
    if (order.find((item) => item.name === product.productName)) {
      //se crea una nueva order indicandole
      /* const newOrder = order.map((productB) => {
        //que si el producto es el mismo
        if (product.productName === productB.productName) {
          //retorne el primer producto aumentado en cantidad y costo actual*/
      return {
        ...productB,
        quantity: productB.quantity + 1,
        cost: productB.cost + product.cost,
      };
      /* }
        return productB;*/

      //});
      console.log("el producto ya existe");
      //setOrder(newOrder);
    }

    // si no existe el producto en la orden se agrega
    else {
      setOrder((order) => [
        ...order,
        {
          name: product.productName,
          quantity: 1,
          cost: product.cost,
        },
      ]);
    }
  };

  const deletingProduct = (id) => {
    //const delProduct = order.filter((product) => product.id !== id);
    console.log("delete");
  };

  const dateAndTime = new Date().toLocaleString();
  const date = new Date().toLocaleDateString();
  //const time = new Date().toLocaleTimeString()

  return (
    <Router>
      <div>
        <Switch>
          <Route path="/role">
            <Role />
          </Route>
          <Route path="/waiter">
            <Waiter date={date} />
          </Route>
          <Route path="/chef">
            <Chef date={date} />
          </Route>
          <Route path="/menu">
            <Menu />
          </Route>
          <Route path="/menu-breakfast">
            <MenuBreakfast
              Data={Data.breakfast}
              order={order}
              addingProductToOrder={addingProductToOrder}
              deletingProduct={deletingProduct}
            />
            <Order order={order} />
          </Route>
          <Route path="/menu-burger">
            <MenuBurger
              Data={Data.burger}
              order={order}
              addingProductToOrder={addingProductToOrder}
              deletingProduct={deletingProduct}
            />
          </Route>
          <Route path="/orders">
            <Orders dateAndTime={dateAndTime} />
          </Route>
          <Route path="/">
            <Main />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
