  <button onClick={()=> deleteProduct()}>Agregar  </button> */
  /* const date = new Date().toDateString() */
  const date = new Date().toLocaleDateString()
  //const time = new Date().toLocaleTimeString()

  return (
    <Router>
      <div>
        <Switch>
          <Route path="/role">
            <Role />
          </Route>
          <Route path="/waiter">
            <Waiter />
          </Route>
          <Route path="/chef">
            <Chef />
          </Route>
          <Route path="/menu">
            <Menu />
          </Route>
          <Route path="/menu-breakfast">
            <MenuBreakfast 
              Data = {Data}
            />
          </Route>
          <Route path="/menu-burger">
            <MenuBurger
              Data = {Data} 
            />
          </Route>
          <Route path="/orders">
            <Orders />
          </Route> 
          <Route path="/">
            <Main 
              date = {date}
            />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
