import React, { useState } from 'react'
import './Navbar.css'

function Navbar() {

    const currentWindow = window.location.pathname
    const changeTitle = () => {
        switch(currentWindow){
            case '/new-order':
                return 'New';
            case '/incoming':
                return 'Incoming';
            case '/ready':
                return 'Ready';
        }
    }

    const [ title, setTitle] = useState(currentWindow)

    return(
        <div className= "topbar">
            <h1>Burguer Queen / {title} </h1>
        </div>
    )
}

export default Navbar
