import React from 'react'
import { AuthContext } from '/Auth'


const PrivateRoute = (props) => {
    const { currentUser, pending } = useContext(AuthContext)

    if (pending) {
        return <div>Loading...</div>
    }

    if (!currentUser) {
        return <Redirect to='/login' />
    }

    return <Route {...props} />
}

export default PrivateRoute
