import React from 'react'

function ToggleMenu({ tab, setTab }) {
    return (
    <div className="container">
        <button onClick={() => setTab('breakfast')} className="btn" style={ { backgroundColor: tab === 'breakfast' ? 'blue' : 'gray' } }>Desayuno</button>
        <button onClick={() => setTab('restOttd')} className="btn" style={ { backgroundColor: tab === 'restOttd' ? 'blue' : 'gray' } }>Comida</button>
    </div>
    )
}

export default ToggleMenu
