import React from 'react'
import { useLocation } from 'react-router-dom'
import './Navbar.css'

function Navbar() {
    const { pathname } = useLocation()
    const changeTitle = () => {
        switch(pathname){
            case '/new-order':
                return 'New';
            case '/incoming':
                return 'Incoming';
            case '/ready':
                return 'Ready';
        }
    }

    let title = changeTitle()

    return(
        <div className= "topbar">
            <h1>Burger Queen / {title} </h1>
        </div>
    )
}

export default Navbar
