import React from "react";

//Router
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

//Styles
//import "./components/utils/styles.css";

//Components
import "./components/utils/styles.module.css";

import Main from "./components/Main";
import Role from "./components/Role";
import Waiter from "./components/Waiter";
import Chef from "./components/Chef";
import Menu from "./components/Menu";
import MenuBreakfast from "./components/Breakfast";
import MenuBurger from "./components/Burger";
import Orders from "./components/Orders";

//JSON
import Data from "./components/utils/Data/Data.json";

function App() {
  /* const [order, setOrder] = useState([])

  const addingProduct = () => {
    //console.log('click');
    setOrder([
      ...order,
      {product} 
    ])
  }

  const deleteProduct = () => {
    //onsole.log('click');
    setOrder([
      ...order,
      {product} 
    ])
  }
  Esto va dentro de return
  <button onClick={()=> addingProduct()}>Agregar  </button>
  <button onClick={()=> deleteProduct()}>Agregar  </button> */
  const dateAndTime = new Date().toLocaleString();
  const date = new Date().toLocaleDateString();
  //const time = new Date().toLocaleTimeString()

  return (
    <Router>
      <div>
        <Switch>
          <Route path="/role">
            <Role />
          </Route>
          <Route path="/waiter">
            <Waiter date={date} />
          </Route>
          <Route path="/chef">
            <Chef date={date} />
          </Route>
          <Route path="/menu">
            <Menu />
          </Route>
          <Route path="/menu-breakfast">
            <MenuBreakfast Data={Data.breakfast} />
          </Route>
          <Route path="/menu-burger">
            <MenuBurger Data={Data.burguer} />
          </Route>
          <Route path="/orders">
            <Orders dateAndTime={dateAndTime} />
          </Route>
          <Route path="/">
            <Main />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
